import React, { useState, useEffect } from "react";
import { render } from "react-dom";
import "./style.css";
import { OrgChartComponent } from "./OrgChart";
import * as d3 from "d3";

const App = props => {
  const [data, setData] = useState(null);
  let addNodeChildFunc = null;
  // let jsonData = require("data.json");

  function addNode() {
    const node = {
      nodeId: "testtt",
      parentNodeId: "O-1",
      width: 330,
      height: 147,
      borderWidth: 1, 
      borderRadius: 5, 
      nodeImage: {
        url:
          "https://raw.githubusercontent.com/bumbeishvili/Assets/master/Projects/D3/Organization%20Chart/general.jpg",
        width: 100,
        height: 100,
        centerTopDistance: 0,
        centerLeftDistance: 0,
        cornerShape: "ROUNDED",
        shadow: true,
        borderWidth: 0,
      },
      nodeIcon: {
        icon: "https://to.ly/1yZnX",
        size: 30
      },
      connectorLineColor: {
        red: 220,
        green: 189,
        blue: 207,
        alpha: 1
      },
      connectorLineWidth: 5,
      dashArray: "",
      expanded: false,
      template: `<div>
                  <div style="margin-left:80px;
                              margin-top:10px;
                              font-size:20px;
                              font-weight:bold;
                         ">Added Root Child </div>
                 <div style="margin-left:80px;
                              margin-top:3px;
                              font-size:16px;
                         ">Added position </div>

                 <div style="margin-left:80px;
                              margin-top:3px;
                              font-size:14px;
                         ">Added unit</div>

                 <div style="margin-left:200px;
                             margin-top:15px;
                             font-size:13px;
                             position:absolute;
                             bottom:5px;
                            ">
                      <div>Added office</div>
                      <div style="margin-top:5px">Added area</div>
                 </div>
              </div>`
    };

    addNodeChildFunc(node);
  }

  function onNodeClick(nodeId) {
    console.log('d3',d3.event)
    alert("clicked " + nodeId);
    var content = tooltipContent(nodeId);
          tooltip.html(content);

          tooltip.transition()
            .duration(200).style("opacity", "1").style('display', 'block');
          d3.select(this).attr('cursor', 'pointer').attr("stroke-width", 50);

          var y = d3.event.pageY;
          var x = d3.event.pageX;

          //restrict tooltip to fit in borders
          if (y < 220) {
            y += 220 - y;
            x += 130;
          }

          if (y > attrs.height - 300) {
            y -= 300 - (attrs.height - y);
          }

          tooltip.style('top', (y - 300) + 'px')
            .style('left', (x - 470) + 'px');
  }

  function tooltipContent(item) {

    var strVar = "";

    strVar += "  <div class=\"customTooltip\">";
    strVar += "    <!--";
    strVar += "    <div class=\"tooltip-image-wrapper\"> <img width=\"300\" src=\"https:\/\/raw.githubusercontent.com\/bumbeishvili\/Assets\/master\/Projects\/D3\/Organization%20Chart\/cto.jpg\"> <\/div>";
    strVar += "-->";
    strVar += "    <div class=\"profile-image-wrapper\" style='background-image: url(" + item.imageUrl + ")'>";
    strVar += "    <\/div>";
    strVar += "    <div class=\"tooltip-hr\"><\/div>";
    strVar += "    <div class=\"tooltip-desc\">";
    strVar += "      <a class=\"name\" href='" + item.profileUrl + "' target=\"_blank\"> " + item.name + "<\/a>";
    strVar += "      <p class=\"position\">" + item.positionName + " <\/p>";
    strVar += "      <p class=\"area\">" + item.area + " <\/p>";
    strVar += "";
    strVar += "      <p class=\"office\">" + item.office + "<\/p>";
    strVar += "     <button class='" + (item.unit.type == 'business' ? " disabled " : "") + " btn btn-tooltip-department' onclick='params.funcs.departmentClick(" + JSON.stringify(item.unit) + ")'>" + item.unit.value + "</button>";
    strVar += "      <h4 class=\"tags-wrapper\">             <span class=\"title\"><i class=\"fa fa-tags\" aria-hidden=\"true\"><\/i>";
    strVar += "        ";
    strVar += "        <\/span>           <ul class=\"tags\">" + getTagsFromCommaSeparatedStrings(item.tags) + "<\/ul>         <\/h4> <\/div>";
    strVar += "    <div class=\"bottom-tooltip-hr\"><\/div>";
    strVar += "  <\/div>";
    strVar += "";

    return strVar;

  }


  useEffect(() => {
    d3.json(
      "https://gist.githubusercontent.com/bumbeishvili/dc0d47bc95ef359fdc75b63cd65edaf2/raw/c33a3a1ef4ba927e3e92b81600c8c6ada345c64b/orgChart.json"
    ).then(data => {
      console.log('dataaaa: ', data)
      setData(data);
    });
  }, [true]);
  return (
    <div>
      Click node to trigger action in parent or &nbsp;
      <button onClick={() => addNode()}>add node as root's child</button>
      <OrgChartComponent
        setClick={click => (addNodeChildFunc = click)}
        onNodeClick={onNodeClick}
        data={data}
      />
    </div>
  );
};

render(<App />, document.getElementById("root"));
